import React, { useEffect, useState } from 'react';
import { FaCheck, FaPencilAlt, FaPlus, FaSearch, FaTrash, FaSort } from 'react-icons/fa';
import { ToastContainer } from 'react-toastify';
import { CreateTask, DeleteTaskById, GetAllTasks, UpdateTaskById } from './api';
import { notify } from './utils';
import 'react-toastify/dist/ReactToastify.css';
import './TaskManager.css';

function TaskManager() {
    const [input, setInput] = useState('');
    const [tasks, setTasks] = useState([]);
    const [copyTasks, setCopyTasks] = useState([]);
    const [updateTask, setUpdateTask] = useState(null);
    const [priority, setPriority] = useState('Medium');
    const [loading, setLoading] = useState(false);
    const [sortCriteria, setSortCriteria] = useState('status');
    const [sortOrder, setSortOrder] = useState('asc');
    const [dueDate, setDueDate] = useState('');

    useEffect(() => {
        fetchAllTasks();
    }, []);

    const fetchAllTasks = async () => {
        setLoading(true);
        try {
            const { data } = await GetAllTasks();
            setTasks(data);
            setCopyTasks(data);
        } catch (err) {
            notify('Failed to fetch tasks', 'error');
        }
        setLoading(false);
    };

    const handleTask = async () => {
        if (!input) return notify('Task name is required', 'warning');
        const obj = {
            taskName: input,
            isDone: false,
            priority,
            dueDate
        };
        try {
            const { success, message } = await CreateTask(obj);
            notify(message, success ? 'success' : 'error');
            fetchAllTasks();
        } catch (err) {
            notify('Failed to create task', 'error');
        }
        setInput('');
        setPriority('Medium');
        setDueDate('');
    };

    const handleDeleteTask = async (id) => {
        if (!window.confirm('Are you sure you want to delete this task?')) return;
        try {
            const { success, message } = await DeleteTaskById(id);
            notify(message, success ? 'success' : 'error');
            fetchAllTasks();
        } catch (err) {
            notify('Failed to delete task', 'error');
        }
    };

    const handleCheckAndUncheck = async (item) => {
        const obj = { ...item, isDone: !item.isDone };
        try {
            const { success, message } = await UpdateTaskById(item._id, obj);
            notify(message, success ? 'success' : 'error');
            fetchAllTasks();
        } catch (err) {
            notify('Failed to update task', 'error');
        }
    };

    const handleSearch = (e) => {
        const term = e.target.value.toLowerCase();
        setTasks(copyTasks.filter(task => task.taskName.toLowerCase().includes(term)));
    };

    const sortTasks = (criteria) => {
        const sorted = [...tasks].sort((a, b) => {
            if (criteria === 'status') return sortOrder === 'asc' ? a.isDone - b.isDone : b.isDone - a.isDone;
            if (criteria === 'priority') {
                const priorityOrder = { High: 3, Medium: 2, Low: 1 };
                return sortOrder === 'asc' ? priorityOrder[a.priority] - priorityOrder[b.priority] : priorityOrder[b.priority] - priorityOrder[a.priority];
            }
            if (criteria === 'dueDate') return sortOrder === 'asc' ? new Date(a.dueDate) - new Date(b.dueDate) : new Date(b.dueDate) - new Date(a.dueDate);
            if (criteria === 'alphabetical') return sortOrder === 'asc' ? a.taskName.localeCompare(b.taskName) : b.taskName.localeCompare(a.taskName);
        });
        setTasks(sorted);
        setSortCriteria(criteria);
        setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    };

    return (
        <div className='container mt-5 task-manager-bg'>
            <nav className='navbar navbar-expand-lg navbar-dark bg-primary mb-4'>
                <div className='container-fluid'>
                    <a className='navbar-brand' href='#'>Project Management Tool</a>
                    <button className='navbar-toggler' type='button' data-bs-toggle='collapse' data-bs-target='#navbarNav'>
                        <span className='navbar-toggler-icon'></span>
                    </button>
                    <div className='collapse navbar-collapse' id='navbarNav'>
                        <ul className='navbar-nav ms-auto'>
                            <li className='nav-item'><a className='nav-link' href='#home'>Home</a></li>
                            <li className='nav-item'><a className='nav-link' href='#tasks'>Tasks</a></li>
                            <li className='nav-item'><a className='nav-link' href='#about'>About</a></li>
                            <li className='nav-item'><a className='nav-link' href='#login'>Login</a></li>
                            <li className='nav-item'><a className='nav-link' href='#signup'>Sign Up</a></li>
                        </ul>
                    </div>
                </div>
            </nav>

            <div id='home'>
                <h1 className='text-center mb-4'>Project Management Tool</h1>
                <div className='task-input-section'>
                    <input type='text' className='form-control me-2' value={input} onChange={(e) => setInput(e.target.value)} placeholder='Add Task' />
                    <select className='form-select me-2' value={priority} onChange={(e) => setPriority(e.target.value)}>
                        <option value='Low'>Low</option>
                        <option value='Medium'>Medium</option>
                        <option value='High'>High</option>
                    </select>
                    <input type='date' className='form-control me-2' value={dueDate} onChange={(e) => setDueDate(e.target.value)} />
                    <button className='btn btn-success' onClick={handleTask}><FaPlus /></button>
                </div>
                <div className='d-flex justify-content-between mb-3'>
                    <div className='input-group'>
                        <span className='input-group-text'><FaSearch /></span>
                        <input type='text' className='form-control' placeholder='Search tasks' onChange={handleSearch} />
                    </div>
                </div>
                {loading ? <p>Loading tasks...</p> : (
                    tasks.map((task) => (
                        <div key={task._id} className='task-item'>
                            <span className={task.isDone ? 'text-decoration-line-through' : ''}>
                                {task.taskName} - <small>{task.priority} Priority</small> - <small>Due: {task.dueDate || 'N/A'}</small>
                            </span>
                            <div>
                                <button className='btn btn-success btn-sm me-2' onClick={() => handleCheckAndUncheck(task)}><FaCheck /></button>
                                <button className='btn btn-danger btn-sm' onClick={() => handleDeleteTask(task._id)}><FaTrash /></button>
                            </div>
                        </div>
                    ))
                )}
            </div>

            <div id='tasks' className='mt-5'>
                <h2>Tasks</h2>
                <p>Manage your tasks efficiently with our project management tool. Add, update, and delete tasks as needed.</p>
            </div>

            <div id='about' className='mt-5'>
                <h2>About</h2>
                <p>This project management tool helps you keep track of your tasks and manage your projects effectively.</p>
            </div>

            <div id='login' className='mt-5'>
                <h2>Login</h2>
                <p>Login to access your account and manage your tasks.</p>
                <input type='text' className='form-control mb-2' placeholder='Username' />
                <input type='password' className='form-control mb-2' placeholder='Password' />
                <button className='btn btn-primary'>Login</button>
            </div>

            <div id='signup' className='mt-5'>
                <h2>Sign Up</h2>
                <p>Create an account to start managing your tasks.</p>
                <input type='text' className='form-control mb-2' placeholder='Username' />
                <input type='email' className='form-control mb-2' placeholder='Email' />
                <input type='password' className='form-control mb-2' placeholder='Password' />
                <button className='btn btn-primary'>Sign Up</button>
            </div>

            <footer className='footer mt-auto py-3 bg-light'>
                <div className='container'>
                    <p className='text-center'>&copy; 2025 Project Management Tool. All rights reserved.</p>
                </div>
            </footer>
            <ToastContainer position='bottom-right' autoClose={3000} hideProgressBar={false} />
        </div>
    );
}

export default TaskManager;
